/**
 * A function for converting hex <-> dec w/o loss of precision.
 *
 * The problem is that parseInt("0x12345...") isn't precise enough to convert
 * 64-bit integers correctly.
 *
 * Internally, this uses arrays to encode decimal digits starting with the least
 * significant:
 * 8 = [8]
 * 16 = [6, 1]
 * 1024 = [4, 2, 0, 1]
 *
 * Source: http://www.danvk.org/hex2dec.html
 */
export declare function add(x: any, y: any, base: any): number[];
export declare function multiplyByNumber(num: any, x: any, base: any): any[] | null;
export declare function parseToDigitsArray(str: any, base: any): number[] | null;
export declare function convertBase(str: any, fromBase: any, toBase: any): string | null;
export declare function decToHex(decStr: any, opts: any): string | null;
export declare function hexToDec(hexStr: any): string | null;
export declare const intToIP: (ip: any) => string;
